import React, { Component } from "react";

export default class CreatePost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      topic: "",
      description: "",
      postCategory: "",
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;

    this.setState({
      ...this.state,
      [name]: value,
    });
  };
  render() {
    return (
      <form className="container" style={{ width: "50%" }}>
        &nbsp;
        <h1>
          <p>Create new Post</p>
        </h1>
        <div class="mb-3">
          <label for="Topic" class="form-label">
            Topic
          </label>
          <input
            type="text"
            class="form-control"
            id="Topic"
            value={this.state.topic}
            onChange={this.handleInputChange}
          />
        </div>
        <div class="mb-3">
          <label for="Description" class="form-label">
            Description
          </label>
          <input
            type="text"
            class="form-control"
            id="Description"
            value={this.state.description}
            onChange={this.handleInputChange}
          />
        </div>
        <div class="mb-3">
          <label for="Category" class="form-label">
            Post Category
          </label>
          <input
            type="text"
            class="form-control"
            id="Category"
            value={this.state.postCategory}
            onChange={this.handleInputChange}
          />
        </div>
        <button type="submit" class="btn btn-primary" onClick={this.onSubmit}>
          <i className="far fa-save"></i>&nbsp; Submit
        </button>
      </form>
    );
  }
}
