import React, { Component } from "react";

export default class CreatePost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      topic: "",
      description: "",
      postCategory: "",
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;

    this.setState({
      ...this.state,
      [name]: value,
    });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const { topic, description, postCategory } = this.state;

    const data = {
      topic: topic,
      description: description,
      postCategory: postCategory,
    };

    console.log(data);
  };
  render() {
    return (
      <form className="container" style={{ width: "50%" }}>
        &nbsp;
        <h1>
          <p>Create new Post</p>
        </h1>
        <div className="mb-3">
          <label for="Topic" className="form-label">
            Topic
          </label>
          <input
            type="text"
            className="form-control"
            onChange={this.handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label for="Description" className="form-label">
            Description
          </label>
          <input
            type="text"
            className="form-control"
            onChange={this.handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label for="Category" className="form-label">
            Post Category
          </label>
          <input
            type="text"
            className="form-control"
            onChange={this.handleInputChange}
          />
        </div>
        <button
          type="submit"
          className="btn btn-primary"
          onClick={this.onSubmit}
        >
          <i className="far fa-save"></i>&nbsp; Submit
        </button>
      </form>
    );
  }
}
